import '../src/styles/App.css';
import React, { Component } from 'react';
import uniqid from 'uniqid';

class App extends Component {
  constructor(){
    super();
    this.state = {
      info: { 
        name: "", 
        email: "",
        phone: "",
        school: "",
        study: "",
        studyDate: "",
        company: "",
        position: "",
        description: "",
        dateFrom: "",
        dateUntil: "",
        id: uniqid() 
      },
    };
  }

  onSumbitInfo = (e) => {
    e.preventDefault();
    this.setState({
      tasks: this.state.tasks.concat(this.state.task),
      task: { text: "", id: uniqid() },
    });
  };

  render(){
    const info = this.state;

    return (
      <>
        <form className="cv-form" onSubmit={this.onSumbitInfo}>
          <section className='general-information'>
            <div> 
              <label htmlFor="nameInput">Name</label>
              <input id="nameInput" name="name" type="text"></input>
            </div>

            <div>
              <label htmlFor="email">Email</label>
              <input id='emailInput' name="email" type="email"></input>
            </div>

              <label htmlFor="phone">Phone Number</label>
              <input id="phoneInput" name="phone" type="tel"></input>
              <small>Format: 123-456-7890</small>
          </section>

          <section className='educational-information'>
            <label htmlFor="schoolName">School Name</label>
            <input id="schoolInput" name="schoolName" type="text"></input>

            <label>Title of Study</label>
            <input type="text"></input>

            <label>Date of Study</label>
            <input></input>
          </section>

          <section className='work-information'>
            <label>Company Name</label>
            <input type="text"></input>

            <label>Position Title</label>
            <input type="text"></input>

            <label>Description of your contributions</label>
            <textarea type="text"></textarea>

            <label>From</label>
            <input></input>

            <label>Until</label>
            <input></input>
          </section>

          <button type="submit">Submit</button>
        </form>
      </>
    );
  }
}

export default App;
